/* @flow */

import { parse } from './parser/index'
import { optimize } from './optimizer'
import { generate } from './codegen/index'
import { createCompilerCreator } from './create-compiler'

/**
 * `createCompilerCreator` allows creating compilers that use alternative parser/optimizer/codegen, 
 * `createCompilerCreator`允许创建使用可选解析器/优化器/代码生成器的编译器
 * e.g the SSR optimizing compiler.
 * Here we just export a default compiler using the default parts.
 * 这里只是导出一个默认的编译器
 * 
 * createCompiler 函数主要通过3个步骤：parse、optimize、generate来生成一个包含ast、render、staticRenderFns的对象。
 */
export const createCompiler = createCompilerCreator(function baseCompile (
  template: string,
  options: CompilerOptions
): CompiledResult {
  // parse
  const ast = parse(template.trim(), options)

  // optimize
  if (options.optimize !== false) {
    optimize(ast, options)
  }

  // generate
  const code = generate(ast, options)

  return {
    ast,
    render: code.render,
    staticRenderFns: code.staticRenderFns
  }
})
